import React, { useState, useEffect } from 'react';

// Dummy data for demo (replace with API in real app)
const allCourses = [
  {
    id: 1,
    title: 'React for Beginners',
    details: 'Learn React from scratch.',
    price: 0,
    uploadDate: '2025-07-12T10:00:00Z',
    fileUrl: '#',
    premium: false,
  },
  {
    id: 2,
    title: 'JavaScript Fundamentals',
    details: 'JS basics and advanced topics.',
    price: 399,
    uploadDate: '2025-07-10T14:30:00Z',
    fileUrl: '#',
    premium: true,
  },
  {
    id: 3,
    title: 'Python + Django Crash Course',
    details: 'Python and Django for web dev.',
    price: 0,
    uploadDate: '2025-07-08T09:15:00Z',
    fileUrl: '#',
    premium: false,
  },
];

const allTests = [
  {
    id: 1,
    title: 'JavaScript Quiz',
    questions: [
      {
        question: 'What is closure in JS?',
        options: [
          'A function inside a function',
          'A variable',
          'A loop',
          'A class'
        ],
        answer: 0
      }
    ]
  },
  {
    id: 2,
    title: 'Firebase Basics',
    questions: [
      {
        question: 'Firebase is a ...',
        options: [
          'Database',
          'Frontend framework',
          'IDE',
          'Browser'
        ],
        answer: 0
      }
    ]
  }
];

export default function StudentDashboard() {
  // Simulate purchased course IDs
  const [purchased, setPurchased] = useState([1]);
  const [attemptTestId, setAttemptTestId] = useState(null);
  const [attemptAnswers, setAttemptAnswers] = useState([]);
  const [timer, setTimer] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [score, setScore] = useState(0);

  // Purchase handler
  const handlePurchase = (id) => {
    setPurchased([...purchased, id]);
  };

  // Test attempt handlers
  const startAttempt = (testId) => {
    setAttemptTestId(testId);
    setAttemptAnswers(Array(allTests.find(t => t.id === testId).questions.length).fill(null));
    setShowResult(false);
    setScore(0);
    setTimer(30); // 30 seconds per test for demo
  };

  useEffect(() => {
    let interval = null;
    if (attemptTestId && timer > 0) {
      interval = setInterval(() => setTimer(t => t - 1), 1000);
    }
    if (timer === 0 && attemptTestId) {
      handleSubmitAttempt();
    }
    return () => clearInterval(interval);
    // eslint-disable-next-line
  }, [attemptTestId, timer]);

  const handleAttemptChange = (qIdx, value) => {
    setAttemptAnswers(prev => {
      const updated = [...prev];
      updated[qIdx] = Number(value);
      return updated;
    });
  };

  const handleSubmitAttempt = () => {
    const test = allTests.find(t => t.id === attemptTestId);
    let correct = 0;
    test.questions.forEach((q, idx) => {
      if (attemptAnswers[idx] === q.answer) correct++;
    });
    setScore(correct);
    setShowResult(true);
    setAttemptTestId(null);
    setTimer(0);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h2 className="text-3xl font-bold text-green-800 mb-6">Student Dashboard</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Purchased Courses */}
        <div className="bg-white p-6 shadow rounded-xl">
          <h3 className="text-xl font-semibold mb-4">Your Purchased Courses</h3>
          <div className="space-y-4">
            {allCourses.filter(c => purchased.includes(c.id)).length === 0 && (
              <div className="text-gray-400">No courses purchased yet.</div>
            )}
            {allCourses.filter(c => purchased.includes(c.id)).map(course => (
              <div key={course.id} className="border rounded p-3">
                <h4 className="font-semibold">{course.title}</h4>
                <p className="text-sm text-gray-600">
                  {course.premium ? 'Premium' : 'Free'} | Uploaded: {new Date(course.uploadDate).toLocaleDateString()}
                </p>
                <button
                  className="mt-2 bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                  onClick={() => window.open(course.fileUrl, '_blank')}
                >
                  Download PDF / Video
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* All Courses (with purchase/download) */}
        <div className="bg-white p-6 shadow rounded-xl">
          <h3 className="text-xl font-semibold mb-4">All Courses</h3>
          <div className="space-y-4">
            {allCourses.map(course => (
              <div key={course.id} className="border rounded p-3 flex justify-between items-center">
                <div>
                  <h4 className="font-semibold">{course.title}</h4>
                  <p className="text-xs text-gray-500">
                    Uploaded: {new Date(course.uploadDate).toLocaleDateString()}
                  </p>
                  <span className={`text-xs font-bold ml-2 ${course.premium ? 'text-green-700' : 'text-blue-700'}`}>
                    {course.premium ? `Premium ₹${course.price}` : 'Free'}
                  </span>
                </div>
                <div>
                  {purchased.includes(course.id) ? (
                    <button
                      className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                      onClick={() => window.open(course.fileUrl, '_blank')}
                    >
                      Download
                    </button>
                  ) : course.premium ? (
                    <button
                      className="bg-purple-600 text-white px-3 py-1 rounded hover:bg-purple-700"
                      onClick={() => handlePurchase(course.id)}
                    >
                      Purchase
                    </button>
                  ) : (
                    <button
                      className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                      onClick={() => setPurchased([...purchased, course.id])}
                    >
                      Get Free
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Available Tests */}
      <div className="mt-10 bg-white p-6 shadow rounded-xl">
        <h3 className="text-xl font-semibold mb-4">Available Tests</h3>
        <ul className="space-y-3">
          {allTests.map(test => (
            <li key={test.id} className="flex justify-between items-center border p-2 rounded">
              <span>{test.title}</span>
              <button
                className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
                onClick={() => startAttempt(test.id)}
              >
                Attempt
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Attempt Test Modal */}
      {attemptTestId && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-xl w-full max-w-lg">
            <h4 className="font-bold mb-2">Test Attempt (Time left: {timer}s)</h4>
            {allTests.find(t => t.id === attemptTestId).questions.map((q, qIdx) => (
              <div key={qIdx} className="mb-3">
                <div className="font-semibold">{qIdx + 1}. {q.question}</div>
                <div>
                  {q.options.map((opt, optIdx) => (
                    <label key={optIdx} className="block">
                      <input
                        type="radio"
                        name={`q${qIdx}`}
                        value={optIdx}
                        checked={attemptAnswers[qIdx] === optIdx}
                        onChange={() => handleAttemptChange(qIdx, optIdx)}
                      /> {opt}
                    </label>
                  ))}
                </div>
              </div>
            ))}
            <button
              className="bg-green-600 text-white px-4 py-2 rounded"
              onClick={handleSubmitAttempt}
            >
              Submit
            </button>
          </div>
        </div>
      )}

      {/* Show result */}
      {showResult && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-xl w-full max-w-sm text-center">
            <h4 className="font-bold mb-2">Test Submitted!</h4>
            <p className="mb-4">Your Score: {score}</p>
            <button className="bg-green-600 text-white px-4 py-2 rounded" onClick={() => setShowResult(false)}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
