import React from 'react';
import { useAuth } from '../context/AuthContext';

export default function AdminDashboard() {
  const { user } = useAuth();

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h2 className="text-3xl font-bold text-blue-800 mb-4">Admin Dashboard</h2>
      <p className="text-gray-700 mb-4">Welcome, {user?.email}</p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Manage Users */}
        <div className="bg-white rounded-xl shadow p-6">
          <h3 className="text-xl font-semibold mb-2">Manage Users</h3>
          <p className="text-sm text-gray-600">Add, remove, or update user roles</p>
          <button className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Add New User</button>
        </div>

        {/* Upload Course */}
        <div className="bg-white rounded-xl shadow p-6">
          <h3 className="text-xl font-semibold mb-2">Upload New Course</h3>
          <form className="space-y-3">
            <input type="text" placeholder="Course Name" className="w-full border px-3 py-2 rounded" />
            <textarea placeholder="Course Description" className="w-full border px-3 py-2 rounded" />
            <input type="number" placeholder="Price" className="w-full border px-3 py-2 rounded" />
            <input type="file" className="w-full" accept="video/*,application/pdf" />
            <button type="submit" className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700">Upload</button>
          </form>
        </div>
      </div>
    </div>
  );
}
