import React, { useState } from 'react';

export default function TeacherDashboard() {
  const [courses, setCourses] = useState([
    {
      id: 1,
      title: 'React for Beginners',
      details: 'Learn React from scratch.',
      price: 499,
      uploadDate: '2025-07-12T10:00:00Z',
    },
    {
      id: 2,
      title: 'JavaScript Fundamentals',
      details: 'JS basics and advanced topics.',
      price: 399,
      uploadDate: '2025-07-10T14:30:00Z',
    },
    {
      id: 3,
      title: 'Python + Django Crash Course',
      details: 'Python and Django for web dev.',
      price: 599,
      uploadDate: '2025-07-08T09:15:00Z',
    },
  ]);

  const [form, setForm] = useState({
    title: '',
    details: '',
    price: '',
    file: null,
  });

  const [editId, setEditId] = useState(null);
  const [editForm, setEditForm] = useState({
    title: '',
    details: '',
    price: '',
  });

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  // Add new course
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.title || !form.price) return;
    setCourses([
      ...courses,
      {
        id: Date.now(),
        title: form.title,
        details: form.details,
        price: form.price,
        uploadDate: new Date().toISOString(),
      },
    ]);
    setForm({ title: '', details: '', price: '', file: null });
  };

  // Start editing
  const editCourse = (id) => {
    const course = courses.find((c) => c.id === id);
    setEditId(id);
    setEditForm({
      title: course.title,
      details: course.details,
      price: course.price,
    });
  };

  // Handle edit input
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Save edit
  const saveEdit = (id) => {
    setCourses(
      courses.map((c) =>
        c.id === id
          ? { ...c, ...editForm }
          : c
      )
    );
    setEditId(null);
  };

  // Cancel edit
  const cancelEdit = () => setEditId(null);

  // Delete course
  const deleteCourse = (id) => {
    setCourses(courses.filter((course) => course.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h2 className="text-3xl font-bold text-purple-800 mb-6">Teacher Dashboard</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Upload Course Section */}
        <div className="bg-white p-6 shadow rounded-xl">
          <h3 className="text-xl font-semibold mb-4">Upload Course</h3>
          <form className="space-y-3" onSubmit={handleSubmit}>
            <input
              type="text"
              name="title"
              value={form.title}
              onChange={handleChange}
              placeholder="Course Title"
              className="w-full px-4 py-2 border rounded"
              required
            />
            <textarea
              name="details"
              value={form.details}
              onChange={handleChange}
              placeholder="Course Details"
              className="w-full px-4 py-2 border rounded"
            />
            <input
              type="number"
              name="price"
              value={form.price}
              onChange={handleChange}
              placeholder="Course Price"
              className="w-full px-4 py-2 border rounded"
              required
            />
            <input
              type="file"
              name="file"
              className="w-full"
              accept="video/*,application/pdf"
              onChange={handleChange}
            />
            <button className="w-full bg-purple-600 text-white py-2 rounded hover:bg-purple-700" type="submit">
              Submit Course
            </button>
          </form>
        </div>

        {/* View Your Courses */}
        <div className="bg-white p-6 shadow rounded-xl">
          <h3 className="text-xl font-semibold mb-4">Your Uploaded Courses</h3>
          <ul className="pl-0 space-y-2 text-gray-700">
            {courses.length === 0 ? (
              <li className="text-gray-400">No courses uploaded yet.</li>
            ) : (
              courses.map((course) => (
                <li key={course.id} className="flex justify-between items-center border-b pb-2">
                  <div>
                    {editId === course.id ? (
                      <div>
                        <input
                          type="text"
                          name="title"
                          value={editForm.title}
                          onChange={handleEditChange}
                          className="border px-2 py-1 rounded mb-1"
                        />
                        <input
                          type="number"
                          name="price"
                          value={editForm.price}
                          onChange={handleEditChange}
                          className="border px-2 py-1 rounded mb-1 ml-2"
                        />
                        <textarea
                          name="details"
                          value={editForm.details}
                          onChange={handleEditChange}
                          className="border px-2 py-1 rounded mt-1 w-full"
                        />
                      </div>
                    ) : (
                      <div>
                        <span className="font-semibold">{course.title}</span>
                        <span className="ml-2 text-xs text-gray-500">
                          Uploaded: {new Date(course.uploadDate).toLocaleDateString()}
                        </span>
                        <span className="ml-2 text-xs text-green-700 font-bold">
                          ₹{course.price}
                        </span>
                        <div className="text-xs text-gray-600">{course.details}</div>
                      </div>
                    )}
                  </div>
                  <div>
                    {editId === course.id ? (
                      <>
                        <button
                          className="text-green-600 mr-2 hover:underline"
                          onClick={() => saveEdit(course.id)}
                        >
                          Save
                        </button>
                        <button
                          className="text-gray-600 hover:underline"
                          onClick={cancelEdit}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <>
                        <button
                          className="text-blue-600 mr-2 hover:underline"
                          onClick={() => editCourse(course.id)}
                        >
                          Edit
                        </button>
                        <button
                          className="text-red-600 hover:underline"
                          onClick={() => deleteCourse(course.id)}
                        >
                          Delete
                        </button>
                      </>
                    )}
                  </div>
                </li>
              ))
            )}
          </ul>
        </div>
      </div>

      {/* Placeholder for Test Feature */}
      <div className="mt-10 bg-white p-6 shadow rounded-xl">
        <h3 className="text-xl font-semibold mb-4">Test Feature (Coming Soon)</h3>
        <p className="text-gray-500">Here you will be able to create and assign tests to your students.</p>
      </div>
    </div>
  );
}
