import React, { useState } from 'react';

export default function TeacherDashboard() {
  const [courses, setCourses] = useState([
    // Example initial data; replace with API data in real app
    {
      id: 1,
      title: 'React for Beginners',
      uploadDate: '2025-07-12T10:00:00Z',
    },
    {
      id: 2,
      title: 'JavaScript Fundamentals',
      uploadDate: '2025-07-10T14:30:00Z',
    },
    {
      id: 3,
      title: 'Python + Django Crash Course',
      uploadDate: '2025-07-08T09:15:00Z',
    },
  ]);

  // Placeholder handlers
  const editCourse = (id) => {
    alert(`Edit course with ID: ${id}`);
  };

  const deleteCourse = (id) => {
    setCourses(courses.filter(course => course.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h2 className="text-3xl font-bold text-purple-800 mb-6">Teacher Dashboard</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Upload Course Section */}
        <div className="bg-white p-6 shadow rounded-xl">
          <h3 className="text-xl font-semibold mb-4">Upload Course</h3>
          <form className="space-y-3">
            <input type="text" placeholder="Course Title" className="w-full px-4 py-2 border rounded" />
            <textarea placeholder="Course Details" className="w-full px-4 py-2 border rounded" />
            <input type="number" placeholder="Course Price" className="w-full px-4 py-2 border rounded" />
            <input type="file" className="w-full" accept="video/*,application/pdf" />
            <button className="w-full bg-purple-600 text-white py-2 rounded hover:bg-purple-700" type="submit">
              Submit Course
            </button>
                </form>
              </div>
      
              {/* View Your Courses */}
              <div className="bg-white p-6 shadow rounded-xl">
                <h3 className="text-xl font-semibold mb-4">Your Uploaded Courses</h3>
                <ul className="pl-0 space-y-2 text-gray-700">
                  {courses.length === 0 ? (
                    <li className="text-gray-400">No courses uploaded yet.</li>
                  ) : (
                    courses.map(course => (
                      <li key={course.id} className="flex justify-between items-center border-b pb-2">
                        <div>
                          <span className="font-semibold">{course.title}</span>
                          <span className="ml-2 text-xs text-gray-500">
                            Uploaded: {new Date(course.uploadDate).toLocaleDateString()}
                          </span>
                        </div>
                        <div>
                          <button
                            className="text-blue-600 mr-2 hover:underline"
                            onClick={() => editCourse(course.id)}
                          >
                            Edit
                          </button>
                          <button
                            className="text-red-600 hover:underline"
                            onClick={() => deleteCourse(course.id)}
                          >
                            Delete
                          </button>
                        </div>
                      </li>
                    ))
                  )}
                </ul>
              </div>
            </div>
          </div>
        );
      }
