import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import CourseManagement from '../components/CourseManagement';
import TestManagement from '../components/TestManagement';
import ResourceManagement from '../components/ResourceManagement';

export default function TeacherDashboard() {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('courses');

  const tabs = [
    { id: 'courses', label: 'Courses', icon: 'üìö' },
    { id: 'tests', label: 'Tests', icon: 'üìù' },
    { id: 'resources', label: 'Resources', icon: 'üìÑ' }
  ];

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-purple-800 mb-2">Teacher Dashboard</h2>
          <p className="text-gray-600">Welcome back, {user?.email}</p>
        </div>

        {/* Navigation Tabs */}
        <div className="bg-white rounded-xl shadow-md mb-6">
          <div className="flex border-b border-gray-200">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex-1 py-4 px-6 text-center font-medium transition-colors ${
                  activeTab === tab.id
                    ? 'text-purple-600 border-b-2 border-purple-600 bg-purple-50'
                    : 'text-gray-500 hover:text-gray-700'
                }`}
              >
                <span className="mr-2">{tab.icon}</span>
                {tab.label}
              </button>
            ))}
          </div>
        </div>

        {/* Tab Content */}
        <div className="bg-white rounded-xl shadow-md p-6">
          {activeTab === 'courses' && <CourseManagement />}
          {activeTab === 'tests' && <TestManagement />}
          {activeTab === 'resources' && <ResourceManagement />}
        </div>
      </div>
    </div>
  );
}
