import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { db } from '../utils/firebase';
import {
  collection,
  getDocs,
  addDoc,
  deleteDoc,
  doc,
  onSnapshot,
  serverTimestamp
} from 'firebase/firestore';

export default function AdminDashboard() {
  const { user } = useAuth();
  const [courses, setCourses] = useState([]);
  const [form, setForm] = useState({ title: '', details: '', price: '', file: null });

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, 'courses'), snapshot => {
      const courseData = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCourses(courseData);
    });
    return () => unsubscribe();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm(prev => ({ ...prev, [name]: files ? files[0] : value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await addDoc(collection(db, 'courses'), {
      title: form.title,
      details: form.details,
      price: Number(form.price),
      uploadDate: serverTimestamp(),
      uploadedBy: 'admin'
    });
    setForm({ title: '', details: '', price: '', file: null });
  };

  const deleteCourse = async (id) => {
    await deleteDoc(doc(db, 'courses', id));
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <form onSubmit={handleSubmit}>
        <input name="title" value={form.title} onChange={handleChange} placeholder="Title" />
        <textarea name="details" value={form.details} onChange={handleChange} placeholder="Details" />
        <input name="price" type="number" value={form.price} onChange={handleChange} placeholder="Price" />
        <button type="submit">Upload</button>
      </form>
      <ul>
        {courses.map(c => (
          <li key={c.id}>{c.title} <button onClick={() => deleteCourse(c.id)}>Delete</button></li>
        ))}
      </ul>
    </div>
  );
}
