import React, { useState, useEffect } from 'react';

export default function TeacherDashboard() {
  const [courses, setCourses] = useState([
    {
      id: 1,
      title: 'React for Beginners',
      details: 'Learn React from scratch.',
      price: 499,
      uploadDate: '2025-07-12T10:00:00Z',
    },
    {
      id: 2,
      title: 'JavaScript Fundamentals',
      details: 'JS basics and advanced topics.',
      price: 399,
      uploadDate: '2025-07-10T14:30:00Z',
    },
    {
      id: 3,
      title: 'Python + Django Crash Course',
      details: 'Python and Django for web dev.',
      price: 599,
      uploadDate: '2025-07-08T09:15:00Z',
    },
  ]);

  const [form, setForm] = useState({
    title: '',
    details: '',
    price: '',
    file: null,
  });

  const [editId, setEditId] = useState(null);
  const [editForm, setEditForm] = useState({
    title: '',
    details: '',
    price: '',
  });

  // Test feature states
  const [tests, setTests] = useState([]);
  const [testForm, setTestForm] = useState({
    title: '',
    questions: [
      { question: '', options: ['', '', '', ''], answer: 0 }
    ]
  });
  const [attemptTestId, setAttemptTestId] = useState(null);
  const [attemptAnswers, setAttemptAnswers] = useState([]);
  const [timer, setTimer] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [score, setScore] = useState(0);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  // Add new course
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.title || !form.price) return;
    setCourses([
      ...courses,
      {
        id: Date.now(),
        title: form.title,
        details: form.details,
        price: form.price,
        uploadDate: new Date().toISOString(),
      },
    ]);
    setForm({ title: '', details: '', price: '', file: null });
  };

  // Start editing
  const editCourse = (id) => {
    const course = courses.find((c) => c.id === id);
    setEditId(id);
    setEditForm({
      title: course.title,
      details: course.details,
      price: course.price,
    });
  };

  // Handle edit input
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Save edit
  const saveEdit = (id) => {
    setCourses(
      courses.map((c) =>
        c.id === id
          ? { ...c, ...editForm }
          : c
      )
    );
    setEditId(null);
  };

  // Cancel edit
  const cancelEdit = () => setEditId(null);

  // Delete course
  const deleteCourse = (id) => {
    setCourses(courses.filter((course) => course.id !== id));
  };

  // Test form handlers
  const handleTestFormChange = (e, qIdx, optIdx) => {
    const { name, value } = e.target;
    setTestForm(prev => {
      const updated = { ...prev };
      if (name === 'title') {
        updated.title = value;
      } else if (name === 'question') {
        updated.questions[qIdx].question = value;
      } else if (name === 'option') {
        updated.questions[qIdx].options[optIdx] = value;
      } else if (name === 'answer') {
        updated.questions[qIdx].answer = Number(value);
      }
      return { ...updated };
    });
  };

  const addQuestion = () => {
    setTestForm(prev => ({
      ...prev,
      questions: [
        ...prev.questions,
        { question: '', options: ['', '', '', ''], answer: 0 }
      ]
    }));
  };

  const handleTestSubmit = (e) => {
    e.preventDefault();
    if (!testForm.title) return;
    setTests([
      ...tests,
      { ...testForm, id: Date.now() }
    ]);
    setTestForm({
      title: '',
      questions: [
        { question: '', options: ['', '', '', ''], answer: 0 }
      ]
    });
  };

  // Attempt test handlers
  const startAttempt = (testId) => {
    setAttemptTestId(testId);
    setAttemptAnswers(Array(tests.find(t => t.id === testId).questions.length).fill(null));
    setShowResult(false);
    setScore(0);
    setTimer(60); // 60 seconds for demo
  };

  useEffect(() => {
    let interval = null;
    if (attemptTestId && timer > 0) {
      interval = setInterval(() => setTimer(t => t - 1), 1000);
    }
    if (timer === 0 && attemptTestId) {
      handleSubmitAttempt();
    }
    return () => clearInterval(interval);
    // eslint-disable-next-line
  }, [attemptTestId, timer]);

  const handleAttemptChange = (qIdx, value) => {
    setAttemptAnswers(prev => {
      const updated = [...prev];
      updated[qIdx] = Number(value);
      return updated;
    });
  };

  const handleSubmitAttempt = () => {
    const test = tests.find(t => t.id === attemptTestId);
    let correct = 0;
    test.questions.forEach((q, idx) => {
      if (attemptAnswers[idx] === q.answer) correct++;
    });
    setScore(correct);
    setShowResult(true);
    setAttemptTestId(null);
    setTimer(0);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h2 className="text-3xl font-bold text-purple-800 mb-6">Teacher Dashboard</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Upload Course Section */}
        <div className="bg-white p-6 shadow rounded-xl">
          <h3 className="text-xl font-semibold mb-4">Upload Course</h3>
          <form className="space-y-3" onSubmit={handleSubmit}>
            <input
              type="text"
              name="title"
              value={form.title}
              onChange={handleChange}
              placeholder="Course Title"
              className="w-full px-4 py-2 border rounded"
              required
            />
            <textarea
              name="details"
              value={form.details}
              onChange={handleChange}
              placeholder="Course Details"
              className="w-full px-4 py-2 border rounded"
            />
            <input
              type="number"
              name="price"
              value={form.price}
              onChange={handleChange}
              placeholder="Course Price"
              className="w-full px-4 py-2 border rounded"
              required
            />
            <input
              type="file"
              name="file"
              className="w-full"
              accept="video/*,application/pdf"
              onChange={handleChange}
            />
            <button className="w-full bg-purple-600 text-white py-2 rounded hover:bg-purple-700" type="submit">
              Submit Course
            </button>
          </form>
        </div>

        {/* View Your Courses */}
        <div className="bg-white p-6 shadow rounded-xl">
          <h3 className="text-xl font-semibold mb-4">Your Uploaded Courses</h3>
          <ul className="pl-0 space-y-2 text-gray-700">
            {courses.length === 0 ? (
              <li className="text-gray-400">No courses uploaded yet.</li>
            ) : (
              courses.map((course) => (
                <li key={course.id} className="flex justify-between items-center border-b pb-2">
                  <div>
                    {editId === course.id ? (
                      <div>
                        <input
                          type="text"
                          name="title"
                          value={editForm.title}
                          onChange={handleEditChange}
                          className="border px-2 py-1 rounded mb-1"
                        />
                        <input
                          type="number"
                          name="price"
                          value={editForm.price}
                          onChange={handleEditChange}
                          className="border px-2 py-1 rounded mb-1 ml-2"
                        />
                        <textarea
                          name="details"
                          value={editForm.details}
                          onChange={handleEditChange}
                          className="border px-2 py-1 rounded mt-1 w-full"
                        />
                      </div>
                    ) : (
                      <div>
                        <span className="font-semibold">{course.title}</span>
                        <span className="ml-2 text-xs text-gray-500">
                          Uploaded: {new Date(course.uploadDate).toLocaleDateString()}
                        </span>
                        <span className="ml-2 text-xs text-green-700 font-bold">
                          â‚¹{course.price}
                        </span>
                        <div className="text-xs text-gray-600">{course.details}</div>
                      </div>
                    )}
                  </div>
                  <div>
                    {editId === course.id ? (
                      <>
                        <button
                          className="text-green-600 mr-2 hover:underline"
                          onClick={() => saveEdit(course.id)}
                        >
                          Save
                        </button>
                        <button
                          className="text-gray-600 hover:underline"
                          onClick={cancelEdit}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <>
                        <button
                          className="text-blue-600 mr-2 hover:underline"
                          onClick={() => editCourse(course.id)}
                        >
                          Edit
                        </button>
                        <button
                          className="text-red-600 hover:underline"
                          onClick={() => deleteCourse(course.id)}
                        >
                          Delete
                        </button>
                      </>
                    )}
                  </div>
                </li>
              ))
            )}
          </ul>
        </div>
      </div>

      {/* Test Feature */}
      <div className="mt-10 bg-white p-6 shadow rounded-xl">
        <h3 className="text-xl font-semibold mb-4">Create Test</h3>
        <form onSubmit={handleTestSubmit} className="space-y-3">
          <input
            type="text"
            name="title"
            value={testForm.title}
            onChange={handleTestFormChange}
            placeholder="Test Title"
            className="w-full px-4 py-2 border rounded"
            required
          />
          {testForm.questions.map((q, qIdx) => (
            <div key={qIdx} className="mb-2 border p-2 rounded">
              <input
                type="text"
                name="question"
                value={q.question}
                onChange={e => handleTestFormChange(e, qIdx)}
                placeholder={`Question ${qIdx + 1}`}
                className="w-full px-2 py-1 border rounded mb-1"
                required
              />
              <div className="flex flex-wrap gap-2">
                {q.options.map((opt, optIdx) => (
                  <input
                    key={optIdx}
                    type="text"
                    name="option"
                    value={opt}
                    onChange={e => handleTestFormChange(e, qIdx, optIdx)}
                    placeholder={`Option ${optIdx + 1}`}
                    className="px-2 py-1 border rounded"
                    required
                  />
                ))}
              </div>
              <div className="mt-1">
                <label className="text-xs mr-2">Correct Option:</label>
                <select
                  name="answer"
                  value={q.answer}
                  onChange={e => handleTestFormChange(e, qIdx)}
                  className="border rounded px-1 py-0.5"
                >
                  {q.options.map((_, idx) => (
                    <option key={idx} value={idx}>{idx + 1}</option>
                  ))}
                </select>
              </div>
            </div>
          ))}
          <button type="button" onClick={addQuestion} className="bg-gray-200 px-2 py-1 rounded mr-2">Add Question</button>
          <button type="submit" className="bg-purple-600 text-white px-4 py-2 rounded">Save Test</button>
        </form>

        <h3 className="text-xl font-semibold mt-8 mb-4">All Tests</h3>
        <ul>
          {tests.length === 0 && <li className="text-gray-400">No tests created yet.</li>}
          {tests.map(test => (
            <li key={test.id} className="mb-2 border-b pb-2 flex justify-between items-center">
              <span className="font-semibold">{test.title}</span>
              <button
                className="bg-blue-600 text-white px-2 py-1 rounded text-xs"
                onClick={() => startAttempt(test.id)}
              >
                Attempt
              </button>
            </li>
          ))}
        </ul>

        {/* Attempt Test Modal */}
        {attemptTestId && (
          <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded shadow-xl w-full max-w-lg">
              <h4 className="font-bold mb-2">Test Attempt (Time left: {timer}s)</h4>
              {tests.find(t => t.id === attemptTestId).questions.map((q, qIdx) => (
                <div key={qIdx} className="mb-3">
                  <div className="font-semibold">{qIdx + 1}. {q.question}</div>
                  <div>
                    {q.options.map((opt, optIdx) => (
                      <label key={optIdx} className="block">
                        <input
                          type="radio"
                          name={`q${qIdx}`}
                          value={optIdx}
                          checked={attemptAnswers[qIdx] === optIdx}
                          onChange={() => handleAttemptChange(qIdx, optIdx)}
                        /> {opt}
                      </label>
                    ))}
                  </div>
                </div>
              ))}
              <button
                className="bg-green-600 text-white px-4 py-2 rounded"
                onClick={handleSubmitAttempt}
              >
                Submit
              </button>
            </div>
          </div>
        )}

        {/* Show result */}
        {showResult && (
          <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded shadow-xl w-full max-w-sm text-center">
              <h4 className="font-bold mb-2">Test Submitted!</h4>
              <p className="mb-4">Your Score: {score}</p>
              <button className="bg-purple-600 text-white px-4 py-2 rounded" onClick={() => setShowResult(false)}>
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
