import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { collection, getDocs, addDoc, doc, updateDoc, query, where } from 'firebase/firestore';
import { db } from '../utils/firebase';

export default function StudentDashboard() {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('courses');
  const [courses, setCourses] = useState([]);
  const [tests, setTests] = useState([]);
  const [resources, setResources] = useState([]);
  const [selectedTest, setSelectedTest] = useState(null);
  const [testAnswers, setTestAnswers] = useState({});
  const [submittedTests, setSubmittedTests] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch data on component mount
  useEffect(() => {
    fetchCourses();
    fetchTests();
    fetchResources();
    fetchSubmittedTests();
  }, [user]);

  const fetchCourses = async () => {
    try {
      const coursesSnapshot = await getDocs(collection(db, 'courses'));
      setCourses(coursesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const fetchTests = async () => {
    try {
      const testsSnapshot = await getDocs(collection(db, 'tests'));
      setTests(testsSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    } catch (error) {
      console.error('Error fetching tests:', error);
    }
  };

  const fetchResources = async () => {
    try {
      const resourcesSnapshot = await getDocs(collection(db, 'resources'));
      setResources(resourcesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    } catch (error) {
      console.error('Error fetching resources:', error);
    }
  };

  const fetchSubmittedTests = async () => {
    try {
      const q = query(collection(db, 'testSubmissions'), where('studentId', '==', user.uid));
      const submissionsSnapshot = await getDocs(q);
      setSubmittedTests(submissionsSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    } catch (error) {
      console.error('Error fetching submitted tests:', error);
    }
  };

  const handleTestSubmit = async (testId) => {
    setLoading(true);
    try {
      // Calculate score
      const test = tests.find(t => t.id === testId);
      let score = 0;
      test.questions.forEach((question, index) => {
        if (testAnswers[index] === question.correctAnswer) {
          score++;
        }
      });
      
      const percentage = (score / test.questions.length) * 100;

      // Save submission
      await addDoc(collection(db, 'testSubmissions'), {
        testId,
        studentId: user.uid,
        studentEmail: user.email,
        answers: testAnswers,
        score,
        totalQuestions: test.questions.length,
        percentage,
        submittedAt: new Date().toISOString(),
        testTitle: test.title
      });

      alert(`Test submitted! Your score: ${score}/${test.questions.length} (${percentage.toFixed(1)}%)`);
      setSelectedTest(null);
      setTestAnswers({});
      fetchSubmittedTests();
    } catch (error) {
      console.error('Error submitting test:', error);
      alert('Error submitting test. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const isTestAlreadySubmitted = (testId) => {
    return submittedTests.some(submission => submission.testId === testId);
  };

  const renderCourses = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {courses.map(course => (
        <div key={course.id} className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg transition-shadow">
          <h3 className="text-xl font-semibold text-gray-800 mb-2">{course.title}</h3>
          <p className="text-gray-600 mb-4">{course.description}</p>
          <div className="flex justify-between items-center">
            <span className="text-green-600 font-bold text-lg">â‚¹{course.price}</span>
            <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors">
              View Details
            </button>
          </div>
        </div>
      ))}
    </div>
  );

  const renderTests = () => (
    <div className="space-y-6">
      {selectedTest ? (
        <div className="bg-white rounded-xl shadow-md p-6">
          <h3 className="text-2xl font-bold text-gray-800 mb-4">{selectedTest.title}</h3>
          <p className="text-gray-600 mb-6">{selectedTest.description}</p>
          
          <div className="space-y-6">
            {selectedTest.questions.map((question, index) => (
              <div key={index} className="border-b pb-4">
                <h4 className="font-semibold text-lg mb-3">
                  {index + 1}. {question.question}
                </h4>
                <div className="space-y-2">
                  {question.options.map((option, optionIndex) => (
                    <label key={optionIndex} className="flex items-center space-x-2 cursor-pointer">
                      <input
                        type="radio"
                        name={`question-${index}`}
                        value={optionIndex}
                        onChange={(e) => setTestAnswers({
                          ...testAnswers,
                          [index]: parseInt(e.target.value)
                        })}
                        className="text-blue-600"
                      />
                      <span>{option}</span>
                    </label>
                  ))}
                </div>
              </div>
            ))}
          </div>
          
          <div className="flex space-x-4 mt-6">
            <button
              onClick={() => handleTestSubmit(selectedTest.id)}
              disabled={loading}
              className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 disabled:opacity-50"
            >
              {loading ? 'Submitting...' : 'Submit Test'}
            </button>
            <button
              onClick={() => setSelectedTest(null)}
              className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
            >
              Cancel
            </button>
          </div>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {tests.map(test => (
            <div key={test.id} className="bg-white rounded-xl shadow-md p-6">
              <h3 className="text-xl font-semibold text-gray-800 mb-2">{test.title}</h3>
              <p className="text-gray-600 mb-4">{test.description}</p>
              <div className="flex justify-between items-center">
                <span className="text-sm text-gray-500">
                  Questions: {test.questions?.length || 0}
                </span>
                {isTestAlreadySubmitted(test.id) ? (
                  <span className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm">
                    âœ“ Completed
                  </span>
                ) : (
                  <button
                    onClick={() => setSelectedTest(test)}
                    className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
                  >
                    Attempt Test
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  const renderResources = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
      {resources.map(resource => (
        <div key={resource.id} className="bg-white rounded-xl shadow-md p-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-2">{resource.title}</h3>
          <p className="text-gray-600 mb-4">{resource.description}</p>
          <div className="flex justify-between items-center">
            <span className="text-sm text-gray-500">
              By: {resource.createdBy || 'Admin'}
            </span>
            <div className="space-x-2">
              {resource.fileUrl && (
                <a
                  href={resource.fileUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 inline-block"
                >
                  Download
                </a>
              )}
              {resource.videoUrl && (
                <a
                  href={resource.videoUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600 inline-block"
                >
                  Watch Video
                </a>
              )}
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  const renderResults = () => (
    <div className="space-y-4">
      <h3 className="text-xl font-semibold text-gray-800">Your Test Results</h3>
      {submittedTests.length === 0 ? (
        <p className="text-gray-500">No tests submitted yet.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {submittedTests.map(submission => (
            <div key={submission.id} className="bg-white rounded-xl shadow-md p-6">
              <h4 className="font-semibold text-lg mb-2">{submission.testTitle}</h4>
              <div className="space-y-2">
                <p className="text-gray-600">
                  Score: {submission.score}/{submission.totalQuestions}
                </p>
                <p className="text-gray-600">
                  Percentage: {submission.percentage.toFixed(1)}%
                </p>
                <p className="text-sm text-gray-500">
                  Submitted: {new Date(submission.submittedAt).toLocaleDateString()}
                </p>
              </div>
              <div className="mt-4">
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className={`h-2 rounded-full ${
                      submission.percentage >= 80 ? 'bg-green-500' : 
                      submission.percentage >= 60 ? 'bg-yellow-500' : 'bg-red-500'
                    }`}
                    style={{ width: `${submission.percentage}%` }}
                  ></div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-3xl font-bold text-green-800 mb-6">Student Dashboard</h2>
        <p className="text-gray-600 mb-8">Welcome, {user?.email}</p>

        {/* Tab Navigation */}
        <div className="flex space-x-1 mb-8 bg-white rounded-lg p-1 shadow-sm">
          {[
            { id: 'courses', label: 'Courses', icon: 'ðŸ“š' },
            { id: 'tests', label: 'Tests', icon: 'ðŸ“' },
            { id: 'resources', label: 'Resources', icon: 'ðŸ“' },
            { id: 'results', label: 'Results', icon: 'ðŸ“Š' }
          ].map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center space-x-2 px-4 py-2 rounded-md font-medium transition-colors ${
                activeTab === tab.id
                  ? 'bg-green-600 text-white'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              <span>{tab.icon}</span>
              <span>{tab.label}</span>
            </button>
          ))}
        </div>

        {/* Tab Content */}
        <div className="min-h-96">
          {activeTab === 'courses' && renderCourses()}
          {activeTab === 'tests' && renderTests()}
          {activeTab === 'resources' && renderResources()}
          {activeTab === 'results' && renderResults()}
        </div>
      </div>
    </div>
  );
}
